// Type definitions for Nexacro N
// Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
// TypeScript Version: 3.8;
    
interface NexacroAPI {
    Layout: nexacro.Layout;
}

    
declare namespace nexacro {
    
	interface Layout  {
	    
	    /**
		 * This property is used to define the user description of a Layout defined in a Form.
		 */
		readonly description?: string;
		/**
		 * This property is used to specify the height of the current layout defined in a form.
		 */
		readonly height?: number;
		/**
		 * This property is used to set the orientation for a layout that will be displayed in a mobile device.
		 */
		readonly mobileorientation?: nexacro.Enum.ScreenOrientation;
		/**
		 * This property is used to define the name of a Layout.
		 */
		readonly name?: string;
		/**
		 * This property is used to set the IDs of screens to which a layout will apply.
		 */
		readonly screenid?: string;
		/**
		 * This property holds the number of step pages that comprise a layout.
		 */
		readonly stepcount?: number;
		/**
		 * This property holds the index of the current step page within a certain layout.
		 */
		readonly stepindex?: number;
		/**
		 * This property is used to specify the width of the current layout defined in a form.
		 */
		readonly width?: number;
	}
}

