// Type definitions for Nexacro N
// Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped
// TypeScript Version: 3.8;
    
interface NexacroAPI {
    Phone: nexacro.Phone;
}

    
declare namespace nexacro {
    
	interface Phone  {
	    /**
		 * @constructor
		 */
		new(): nexacro.Phone;
	    /**
		 * This static method opens the phone dialer of a mobile device with the phone number delivered as an argument.
		 * @param {string} strPhoneNumber This is a string that represents the phone number of a person to contact.
		 * @param {boolean} bAutoDialing If you specify "true", a phone call will proceed automatically after the dialer is opened.
If you specify "false", just the dialer will be opened without an automatic phone call.

The default value is "false".
In the iOS/iPadOS NRE, this parameter will work as if it is set to "false" regardless of an actual value.
		 * @returns {boolean} 
		 */
		makeCall(strPhoneNumber: string, bAutoDialing?: boolean): boolean;
	}
}

