interface NexacroAPI {
    Screen: nexacro.Screen;
}

    
declare namespace nexacro {
    
	interface Screen  {
	    
	    /**
		 * This property is used to specify the ID of a screen.
		 */
		readonly id?: string;
		/**
		 * This property is used to specify a locale, which serves as a base for selecting a screen for executing an application.
		 */
		readonly specifiedlocale?: nexacro.Enum.LocaleCode;
		/**
		 * This property is used to specify an OS, which serves as a base for selecting a screen for executing an application.
		 */
		readonly specifiedos?: nexacro.Enum.SystemOs;
		/**
		 * This property is used to set a device type, which serves as a base for selecting a screen where an application runs.
		 */
		readonly type?: nexacro.Enum.ScreenType;
		/**
		 * This property is used to determine whether to use the pinch zoom function. This property is applicable when an application whose screen type is "desktop" is loaded onto a mobile web browser.
		 */
		readonly userzoom?: boolean;
		/**
		 * This property is used to determine the base for the zoom feature applied when an application is loaded in the mobile environment.
		 */
		readonly zoomfactortype?: nexacro.Enum.Screen_ZoomFactorType;
	}
}

